create table team_list(team_id bigint auto_increment primary key, ground_id bigint not null, admin_id vARCHAR(36) NOT NULL, team_name varchar(255) not null, foreign(ground_id) references ground_list(ground_id), foreign key(admin_id) references admin_list(admin_id));
create table game_schedule_list(game_id bigint auto_increment primary key, ground_id bigint not null, admin_id vARCHAR(36) NOT NULL, h_team_id bigint not null, a_team_id bigint not null, win bigint, foreign key(ground_id) references ground_list(ground_id), foreign key(admin_id) references admin_list(admin_id));
create table game_res_list(game_res_id bigint auto_increment primary key, user_id varchar(36) not null, game_id bigint NOT NULL, game_res_count bigint not null, game_res_date datetime not null, foreign key(user_id) references user_list(user_id), foreign key(game_id) references game_schedule_list(game_id));
